SELECT streamers.id, streamers.name, streamers.email, streamers.twitter, streamers.twitter_followers, streamers.instagram, streamers.instagram_followers, streamers.twitch_url, streamers.twitch_subs, streamers.youtube_url, streamers.youtube_subs, streamers.country, streamers.prev_7_day_views, streamers.percentage_growth, genres_1.id AS id_1, genres_1.name AS name_1 
FROM streamers JOIN genre_streamer_associations ON streamers.id = genre_streamer_associations.streamer_id JOIN genres ON genres.id = genre_streamer_associations.genre_id LEFT OUTER JOIN (genre_streamer_associations AS genre_streamer_associations_1 JOIN genres AS genres_1 ON genres_1.id = genre_streamer_associations_1.genre_id) ON streamers.id = genre_streamer_associations_1.streamer_id
